const Dashboard = (props) => {
    const { users } = props;
    //const[page, setPage]=React.useState(0);
    const [listEtudiant, setListEtudiant] = React.useState ([]);
  //const [rowsPerPage, setRowsPerPage] = React.useState(10);

  /*const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };*/

  /*const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
    };*/
    const  getEtudiant = () => {
       axios.get("http://localhost:8080/etudiant",
           {
           headers:{"token":`Bearer ${getToken ()}` }
       })
            .then(res => {
                setListEtudiant(res.data)
                //console.log(listEtudiant)
            })
            .catch(err => {
                console.log(err);
            })
    }
    React.useEffect (() =>
        getEtudiant(),
        []
    )
    //console.log(props);
    return (
        <>
            <SideBar/>
      <div className="tablestyle">
    <Paper sx={{ width: '100%', overflow: 'hidden' }}>
      <TableContainer sx={{ maxHeight: 440 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
                <TableCell>
                    Firstname
                </TableCell>
                <TableCell>
                    Lastname
                </TableCell>
                <TableCell>
                    Date Naissance
                </TableCell>
                <TableCell>
                     note1
                </TableCell>
                <TableCell>
                     note2
                </TableCell>
                <TableCell>
                    Détail
                </TableCell>                     
            </TableRow>
          </TableHead>

            {listEtudiant
              .map(index => (
                  <TableBody>

                  <TableRow>

                        <TableCell>
                          {index.firstName}
                          </TableCell>
                          <TableCell>
                          {index.lastName}
                          </TableCell>
                          <TableCell>
                          {index.dateNaissance}
                          </TableCell>
                          <TableCell>
                          {index.note1}
                          </TableCell>
                          <TableCell>
                          {index.note2}
                          </TableCell>
                         <TableCell>
                            {/*pathname: `/edit/${index.id}`,*/}
                                    <Link
                                        to={{

                                            pathname: `/edit/${index.idEtu}`,
                                            state: { index }
                                        }}
                                    >
                                  {/*<i
											className="icofont-edit-alt"
											style={{ color: "#4cba75", fontSize: 20 }}
                                    />*/}
                        {/*< i class="icofont-edit-alt" style={{ color: "red", fontSize: 20 }} />*/}
                                    modifier
                                    </Link>

                                    <Link
                                        to={{
                                            pathname: "/delete",
                                            state: { index }
                                        }}
                                    >
                                    <i
											className="icofont-ui-delete"
											style={{ color: "red", fontSize: 20 }}
									    />
                                    </Link>

                        </TableCell>  
                      </TableRow>

                  </TableBody>

                  ))}
        </Table>
      </TableContainer>
                </Paper>
                <div>
                    {/*console.log(listEtudiant[0])*/}
                </div>
            </div>
            </>
  );
}
export default Dashboard;
