    const { idEtu } = useParams();
    const [listEtudiant, setListEtudiant] = useState([]);

    const getEtudiant = () => {
         axios.get(`http://localhost:8080/etudiant/${idEtu}`
            /*{
                headers: { "token": `Bearer ${getToken()}` }
            }*/)
            .then(res => {
                setListEtudiant(res.data)
                //console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            })
    }
    useEffect( () =>
        getEtudiant(),
        []
    )

    return (
        <>
        <div>

                          {/*listEtudiant.map(index => (
