import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'
import axios from 'axios'
import { getToken } from '../utils/localStorageUtil';

const EditEtudiant = () => {
    const { idEtu } = useParams();
    const [etudiants, setEtudiant] = useState([]);
    const getEtudiant = async () => {
        await axios.get(`http://localhost:8080/etudiant/${idEtu}`
            /*{
                headers: { "token": `Bearer ${getToken()}` }
            }*/)
            .then(res => {
                setEtudiant(res.data)
                //console.log(res.data)
            })
            .catch(err => {
                console.log(err);
            })
    }
    useEffect(() =>
        getEtudiant(),
        []
    )

    return (
        <div>




                          {etudiants.map(index => (
                                  <ul>
                                      <li>{index.firstName}</li>
                                  </ul>
                              ))}




        </div>
    );
};

export default EditEtudiant;
